mapper.select:
select count(user_id)
from user
where user_id = #{user_id}

select *
from user
where user_id = #{user_id} and password = #{password}

mapper.update:
update user
set iduser = #{iduser}, rate_idrate = #{rate_idrate}, user_name = #{user_name}, user_id = #{user_id}, password = #{password} 
where user_id = #{user_id}
